import httpService from '@/service/httpService'

export default {
  // 新建全民推广广告
  creatNewAD(input) {
    return httpService.post('/front/spread', input)
  },
  // 删除全民推广广告
  deleteAD(id) {
    return httpService.delete(`/front/spread/${id}`)
  },
  // 编辑全民推广广告
  editAD(id, params) {
    return httpService.put(`/front/spread/${id}`, params)
  },
  // 获取单个推广的广告
  getSingleAD(id) {
    return httpService.get(`/front/spread/${id}`)
  },
  // 获取发布管理广告列表
  getPublishList(page) {
    return httpService({
      method: 'get',
      loading: false,
      params: { page },
      url: '/front/get_spread_list'
    })
  },
  // 获取今日分享次数
  getRewardCount() {
    return httpService({
      loading: false,
      url: '/front/spread_share_num'
    })
  },
  // 获取全民推广推广列表
  getSpreadList(page) {
    return httpService({
      method: 'get',
      loading: false,
      params: { page },
      url: '/front/spread'
    })
  },
  // 更新全民推广的浏览量
  updateVisited(id) {
    httpService({
      method: 'get',
      params: { id },
      loading: false,
      url: '/front/spread_browse_num'
    })
  },
  // 获取全民推广的分享记录
  getShareHistory(params, loading) {
    return httpService({
      params,
      url: '',
      method: 'get',
      loading: loading || true
    })
  },
  // 分享广告
  shareAD(id) {
    return httpService({
      method: 'get',
      loading: false,
      params: { spread_id: id },
      url: '/front/spread_share'
    })
  },
  // 获取分享历史记录
  getShareHistory(page) {
    return httpService({
      method: 'get',
      params: { page },
      url: '/front/spread_share_list'
    })
  }
}
