import httpService from '../service/httpService.js'
import store from '../store/index.js'
import User from '../api/user'
import config from '../config/index'
/* 
    判断前往的路径是否为分享路径
*/
const isShare = path => {
  let regs = config.sharePath.map(item => new RegExp('^' + item))
  return regs.some(item => item.test(path))
}
/*判断是否为ios设备
 *@method isIos
 *@param  无
 *@return {Boolean} true  为ios false 安卓
 */
const isIos = () => {
  let u = navigator.userAgent
  return !!u.match(/\(i[^;]+;( U;)? CPU.+Mac OS X/)
}
/*获取上传oss配置
 *@method getOssConfig
 *@param  无
 *@return {object} 返回上传需要的oss配置信息
 */
const getOssConfig = () => {
  httpService({
    loading: false,
    url: '/front/get-web-policy'
  }).then(res => {
    store.commit('system/updateOssConfig', res)
  })
}

/*抓取营销网页的第一张图片作为封面
 *@method getMarketingCover
 *@param {String} input 营销网页内容
 *@return {String} 返回封面图片地址
 */
const getMarketingCover = input => {
  if (typeof input === 'string') {
    input = JSON.parse(input)
  }
  input = Array.isArray(input) ? input : input.content
  for (let i = 0; i < input.length; i++) {
    if (input[i].type === 'img') {
      return input[i].src
    }
  }
  return window.host + '/static/logo.png'
}

/*个人网页(固定模板和自定义模板)
 *@method pageCover
 *@param {object} input 后台返回的关于网页的相关数据
 *@return {String} 返回封面图片地址
 */
const pageCover = input => {
  try {
    if (input.type === 1) {
      let content = JSON.parse(input.content)
      let cover = content[0].backImg.src || content[0].backImg
      return /^http/.test(cover) ? cover : '/' + cover
    } else if (input.type === 2) {
      let content = JSON.parse(input.content)
      for (let i = 0; i < content.length; i++) {
        if (content[i].type === 'slider') {
          return content[i].children[0].img.src
        }
      }
      return window.host + '/static/cover_custom.png'
    } else {
      return /^\[/.test(input.cover_path)
        ? JSON.parse(input.cover_path)[0]
        : input.cover_path
    }
  } catch (err) {
    return window.host + '/static/cover_custom.png'
  }
}

/* 
    获取文章分享封面
*/
const getArticleCover = input => {
  try {
    return JSON.parse(input)[0]
  } catch (err) {
    return input
  }
}

/*设置cookie
 *@method setCookie
 *@param {String} val token
 *@return 无
 */
const setCookie = val => {
  store.commit('system/setToken', val)
}

/*得到cookie
 *@method getCookie
 *@param 无
 *@return {String} cookie值
 */
const getCookie = () => {
  return store.state.system.token
}
/*
  @params
    key
*/
const delCookie = key => {
  store.commit('system/setToken', null)
}
/*获取url指定名称的参数
 *@method getParamsFromUrl
 *@param {Srting} paramName 想获取的参数的名称
 *@return {String} 指定名称参数结果
 */
const getParamsFromUrl = paramName => {
  var args = new Object()
  var argsStr = location.href.substring(location.href.indexOf('?')) //获取URL参数字符串
  if (argsStr.length > 0) {
    argsStr = argsStr.substring(1)
    var nameValueArr = argsStr.split('&') //多参数
    for (var i = 0; i < nameValueArr.length; i++) {
      var pos = nameValueArr[i].indexOf('=')
      if (pos == -1) continue //如果没有找到就跳过
      var argName = nameValueArr[i].substring(0, pos) //提取name
      var argVal = nameValueArr[i].substring(pos + 1) //提取value
      args[argName] = unescape(argVal)
    }
    return args[paramName]
  }
}
/*获取系统信息以及个人信息
 *@method getSystemInfo
 *@param
 *@return
 */
const getSystemInfo = async () => {
  getOssConfig()
  let token = getParamsFromUrl('token')
  let platform = getParamsFromUrl('platform')
  store.commit('system/setPlatform', platform || 'wechat')
  store.commit('system/setToken', token || store.state.system.token || '')
  if (store.state.system.platform !== 'wechat') {
    if (token || !store.state.user.userInfo) {
      User.getProfile()
    }
  }
}
export {
  isIos,
  isShare,
  pageCover,
  setCookie,
  delCookie,
  getCookie,
  getOssConfig,
  getSystemInfo,
  getArticleCover,
  getParamsFromUrl,
  getMarketingCover
}
