import store from '../store';
import androidSdk from '../jssdk/androidSdk';
/** 
      options: {
        link: link,                         // 分享链接 *
        desc: desc,                         // 分享描述 *
        title: title,                       // 分享标题 *
        imgUrl: imgUrl,                     // 分享封面 *
        cancel: function() {},
        success: function() {},
        type: '',                           // 分享类型,music、video或link，不填默认为link
        dataUrl: '',                        // 如果type是music或video，则要提供数据链接，默认为空
      }
 * 
 * 
*/

var shareService = function shareService(options) {
  var platform = store.state.system.platform;

  switch (platform) {
    case 'wechat':
      store.commit('system/shareGuide', true);
      break;

    case 'android':
      androidSdk.nativeShare(options);
      break;

    case 'webApp':
      store.commit('app/updateAppShare', options);
      break;

    default:
      break;
  }
};

export default shareService;
