/**
*  我的网页改版
*/

<template>
  <Wrapper @scrolling="nextPage" class="wraper" :paddingTop="paddingTop">
    <AppHeader ref="header" title="个人微网">
    </AppHeader>
    <AppLoading v-show="firstIn"></AppLoading>
    <p>
      <router-link tag="span" to="/activity/product" class="WXproductValue"><i class="iconfont icon-help-copy"></i> 产品价值</router-link>
    </p>
    <p @click="toPage('do')" v-show="!allList.length&&!firstIn" class="lack"><img src="./lack.jpg" /></p>
    <!-- 列表 -->
    <div class="marketing-item" @click="goMarketingDetail(index)" v-for="(item, index) in allList" :key="index">
      <img src="./moren.png" class="default" v-if="item.is_default" />
      <div class="marketing-item-poster">
        <img :src="getItemCover(item)" />
      </div>
      <div class="marketing-item-info">
        <p class="marketing-item-title" v-text="item.title"></p>
        <p class="marketing-item-counter">
          <span class="marketing-item-visited">
            <i class="iconfont icon-chakan"></i>
            <em>{{item.popularity}}</em>
          </span>
          <span class="marketing-item-shared">
            <i class="iconfont icon-fenxiang2"></i>
            <em>{{item.share}}</em>
          </span>
        </p>
        <p class="marketing-item-date" v-text="item.created_at.substring(0,10)"></p>
        <p class="marketing-item-operate">
          <span class="marketing-item-share" @click.stop="goMarketingDetail(index,true)">
            <i class="iconfont icon-fenxiang"></i>
            分享
          </span>
          <span class="marketing-item-manager" @click.stop="manage(item.type,index)">
            <i class="iconfont icon-shanchu"></i>
            管理
          </span>
        </p>
      </div>
    </div>
    <!-- loading+底部选项 -->
    <AppLoading v-if="loading&&allList.length>6" style="background:transparent;" type="list"></AppLoading>
    <div ref="footer" class="footBar">
      <div class="button button-do" @click="toPage('do')">去制作</div>
      <div class="button button-clone" @click="toPage('clone')">去克隆</div>
    </div>
    <!-- 底部弹出 -->
    <Operator :show="showOperator" v-model="showOperator">
      <div v-if="selectInfo.type===2" class="operate" @click="cloneSetting(selectInfo.index)">
        <div class="operate-icon">
          <i class="iconfont icon-kelongshezhi"></i>
        </div>
        <p class="operate-text">克隆设置</p>
      </div>
      <div class="operate" @click="editPage('allList', selectInfo.type, selectInfo.index)">
        <div class="operate-icon">
          <i class="iconfont icon-bianji"></i>
        </div>
        <p class="operate-text">修改</p>
      </div>
      <div class="operate" @click="setDefault('allList', selectInfo.type, selectInfo.index)">
        <div class="operate-icon">
          <i class="iconfont icon-sheweimorenwangye"></i>
        </div>
        <p class="operate-text">设为默认</p>
      </div>
      <div class="operate" @click="delPage('allList', selectInfo.type, selectInfo.index)">
        <div class="operate-icon">
          <i class="iconfont icon-shanchuxiao"></i>
        </div>
        <p class="operate-text">删除</p>
      </div>
    </Operator>
    <!-- 克隆设置 -->
    <CloneDialog v-if="show&&allList.length" @closeHandle="closeHandle"></CloneDialog>
  </Wrapper>
</template>

<script>
import CloneDialog from '@/components/CloneDialog'
import dialogService from '@/service/dialogService'
import Operator from '../../../components/Operator'
import AppHeader from '../../../components/AppHeader/index'
import Wrapper from '../../../components/Wrapper/index'
import AppLoading from '../../../components/AppLoading/index'
import httpService from '@/service/httpService'
import { mapState, mapActions } from 'vuex'

export default {
  name: 'webPageMine',
  data() {
    return {
      windowHeight: null, //高度
      loading: false, // 是否在加载
      last_page: null, // 总页数
      page: 1, // 当前页数
      firstIn: true, //第一次进入
      showOperator: false, //管理底部弹出
      show: false, // clone
      selectInfo: { type: null, index: null }, //底部弹出选择
      allList: [] //列表
    }
  },
  components: {
    AppHeader,
    Wrapper,
    Operator,
    CloneDialog,
    AppLoading
  },
  computed: {
    ...mapState({
      userInfo: state => state.user.userInfo,
      platform: state => state.system.platform
    }),
    paddingTop: function() {
      return this.platform === 'wechat' ? '0px' : '100px'
    }
  },
  activated() {
    this.getlist()
  },
  mounted() {
    this.getlist()
    let _self = this
  },
  methods: {
    ...mapActions(['system/updateBeVipPath']),
    // 获取列表
    async getlist(load) {
      this.loading = true
      if (load) this.page += 1
      let res = await httpService.get('/front/personal_page/all', {
        page: this.page
      })
      if (res.status !== 0) {
        dialogService.toast(res.message)
        return
      }
      if (load) {
        this.allList = this.allList.concat(res.data.data)
      } else {
        this.allList = res.data.data
        this.firstIn = false
      }
      this.last_page = res.data.last_page
      this.windowHeight = document.documentElement.clientHeight
      this.loading = false
    },
    nextPage(scrolled, scrollHeight) {
      let scrollBottom = this.windowHeight + scrolled > scrollHeight - 100
      if (!scrollBottom || this.loading || this.page >= this.last_page) {
        return false
      } else {
        this.getlist(true)
      }
    },
    // 管理按钮
    manage(type, index) {
      this.showOperator = true
      this.selectInfo.type = type
      this.selectInfo.index = index
    },
    // 开通会员操作
    _openVip(msg) {
      let _self = this
      dialogService.confirm('开通会员才能制作多个网页哦，是否去开通？', ok => {
        if (ok) {
          _self['system/updateBeVipPath']('/webPage/mine')
          _self.$router.push({
            path: '/mine/vipCenter'
          })
        }
      })
    },
    // 跳转页面
    toPage(where) {
      if (where == 'do') {
        this.userInfo.vip_level
          ? this.$router.push('/webPage/edit')
          : this.allList.length > 0
          ? this._openVip()
          : this.$router.push('/webPage/edit')
      } else if (where == 'clone') {
        this.$router.push(`/cloneRank`)
      }
    },
    // 跳转到自定义网页详情
    goMarketingDetail(index, showGuide) {
      this.$router.push(`/webPage/detail/${this.allList[index].id}`)
    },
    // 克隆弹窗
    async closeHandle(input) {
      if (input) {
        if (this.cloneSettingId) {
          let res = await httpService.put(
            `/front/personal_page/customs/${this.cloneSettingId}`,
            input
          )
          if (res.status !== 0) dialogService.alert(res.message)
          // 修改成功后操作
          dialogService.toast('修改成功')
          this.showOperator = false
          this.show = false
        }
      } else {
        this.show = false
      }
    },
    // 设置默认
    async setDefault(key, type, index) {
      if (this[key][index].is_default) {
        this.showOperator = false
        return false
      }
      try {
        let res = await httpService.post(
          `/front/personal_page/user_default_temp/${this[key][index].id}`
        )
        httpService.get('/front/inter-push-info', {
          type: 'edit',
          interpush_type: 1,
          interpush_article: this[key][index].id
        })
        if (res.status !== 0) throw new Error(res.message)
        this[key] = this[key].map((item, _index) => {
          item.is_default = index === _index ? 1 : 0
          return item
        })
        this.showOperator = false
        dialogService.alert('设置成功')
      } catch (err) {
        dialogService.alert(err.message)
      }
    },
    // 编辑网页
    editPage(key, type, index) {
      this.showOperator = false
      if (type === 1) {
        this.$router.push({
          path: `/tools/personal/template/${
            this[key][index].template_id
          }?edit=1`
        })
      }
      if (type === 2) {
        this.$router.push({
          path: `/webPage/edit/${this[key][index].id}`
        })
      }
    },
    // 删除网页
    delPage(key, type, index) {
      let _self = this
      const _delpage = async function() {
        try {
          if (type === 1) {
            let res = await httpService.delete(
              `/front/personal_page/user_temp/${_self[key][index].id}`
            )
            if (res.status !== 0) throw new Error(res.message)
          }
          if (type === 2) {
            let res = await httpService.delete(
              `/front/personal_page/customs/${_self[key][index].id}`
            )
            if (res.status !== 0) throw new Error(res.message)
          }
          dialogService.alert('删除成功！')
          _self.showOperator = false
          _self[key].splice(index, 1)
        } catch (err) {
          dialogService.alert(err.message)
        }
      }
      dialogService.confirm('您确定要删除该模板吗？', function(ok) {
        _self.showOperator = false
        if (ok) {
          _delpage()
        }
      })
    },
    // 获取当前网页的封面
    getItemCover(item) {
      let cover = '/static/cover_custom.1.png'
      //   item.type = 1
      if (item.type === 1) {
        switch (item.cate) {
          case '推荐':
            cover = '/static/cover_tuijian.png'
            break
          case '节日':
            cover = '/static/cover_jieri.png'
            break
          case '商务':
            cover = '/static/cover_shangwu.png'
            break
          case '微商':
            cover = '/static/cover_weishang.png'
            break
          case '科技':
            // cover = '/static/cover/cover_keji.png'
            break
          default:
            break
        }
      } else {
        let content = JSON.parse(item.content)
        for (let i = 0; i < content.length; i++) {
          if (content[i].type === 'slider' && content[i].isShow) {
            cover = content[i].children[0].img.src
            break
          }
        }
      }
      return cover
    },
    // 克隆设置
    cloneSetting(index) {
      this.cloneSettingId = this.allList[index].id
      this.show = true
      this.showOperator = false
    },
    // 去相应页面
    goModal(type) {
      if (type === 1) {
        this.$router.push({
          path: `/tools/personal`
        })
      } else {
        this.$router.push({
          path: `/tools/personal/customIndex`
        })
      }
    }
  }
}
