import axios from 'axios'
import store from '../store/index.js'
import dialogService from './dialogService'
import {
  sendError
} from '../utils/tools'

const generateHeaders = () => {
  let Authorization = 'Bearer '
  let token = store.state.system.token || ''
  return {
    'Content-Type': 'application/json',
    Authorization: Authorization + token
  }
}
const httpService = input => {
  if (input.loading !== undefined) {
    store.commit('system/updateHttpLoading', input.loading)
  } else {
    if (input.method !== 'get' && input.method !== undefined) {
      store.commit('system/updateHttpLoading', true)
    }
  }

  input.url = /^http:\/\/|^https:\/\//.test(input.url) ?
    input.url :
    (window.api || '') + input.url
  // 构建 headers
  input.headers = Object.assign(generateHeaders(), input.headers)

  return new Promise(resolve => {
    axios({
        url: input.url,
        timeout: 30000,
        data: input.body,
        params: input.params,
        headers: input.headers,
        method: input.method || 'get'
      })
      .then(res => {
        store.commit('system/updateHttpLoading', false)
        resolve(res.data)
      })
      .catch(err => {
        store.commit('system/updateHttpLoading', false)
        if (err.code === 'ECONNABORTED') {
          dialogService.alert('请求超时，请检查您的网络')
        } else if (err.response.status === 401) {
          // 跳转到登录
          dialogService.alert('您的登陆凭证已过期,请重新登陆', function () {
            store.commit('system/setToken', null)
            store.commit('user/setUserInfo', null)
            location.href = window.host + '/#/login'
          })
        } else if (err.response.status === 500) {
          errorLog(err)
          dialogService.alert('服务器内部错误,请稍后再试')
        } else {
          resolve(err.data)
        }
      })
  })
}
httpService.get = (url, params) => {
  return httpService({
    url,
    params
  })
}

httpService.post = (url, params) => {
  return httpService({
    url,
    body: params,
    method: 'post'
  })
}

httpService.delete = (url, params) => {
  return httpService({
    url,
    body: params,
    method: 'delete'
  })
}

httpService.put = (url, params) => {
  return httpService({
    url,
    body: params,
    method: 'put'
  })
}
export default httpService
