import httpService from '../../service/httpService'

export default {
  // 关注
  async follow(id) {
    return httpService.post(`/front/follow/media/${id}`)
  },
  // 取消关注
  async unfollow(id) {
    return httpService.post(`/front/un_follow/media/${id}`)
  },
  // 评论
  async addcomment(id, content) {
    return httpService({
      url: `/front/articles/${id}/comments`,
      method: 'post',
      body: {
        content: content
      }
    })
  },
  // 文章收藏
  async collect(id, isCollect) {
    if (isCollect) {
      return httpService({
        url: `/front/articles/${id}/favorite`,
        method: 'post'
      })
    } else {
      return httpService({
        url: `/front/articles/favorite`,
        method: 'delete',
        body: {
          ids: [id]
        }
      })
    }
  },
  // 评论点赞
  async thumb(id) {
    return httpService({
      url: `/front/thumb/comment/${id}`,
      method: 'post'
    })
  },
  // 取消点赞
  async unthumb(id) {
    return httpService({
      url: `/front/un_thumb/comment/${id}`,
      method: 'post'
    })
  },
  // 文章点赞
  async thumbArticle(id) {
    return httpService({
      url: `/front/thumb/article/${id}`,
      method: 'post'
    })
  },
  // 取消文章点赞
  async unthumbArticle(id) {
    return httpService({
      url: `/front/un_thumb/article/${id}`,
      method: 'post'
    })
  },
  // 文章投诉
  async complaint(id, obj) {
    return httpService.post(`/front/complaint/article/${id}`, obj)
  },
  // 收藏名片
  async collectCard(id) {
    return httpService({
      url: `/front/card/holder`,
      method: 'post',
      body: {
        card_id: id
      }
    })
  },
  // 收藏文章
  async collectionArticle(item) {
    return httpService.get(`/front/articles/favorite`, item)
  },
  // 删除收藏文章
  async delArticle(item) {
    return httpService.delete(`/front/articles/favorite`, {
      ids: item
    })
  },
  // 获取评论列表
  async getComment(id, page) {
    return httpService({
      url: `/front/articles/${id}/comments`,
      params: {
        page: page,
        limit: 10
      }
    })
  },
  // 根据文章id获取文章详情
  async getArticleById(id) {
    return httpService.get(`/front/get-article/${id}`)
  },
  // 获取推荐文章
  async getRecommand(id) {
    return httpService({
      method: 'post',
      url: '/front/get-article-relation',
      body: {
        article_category_uuid: id
      },
      loading: false
    })
  },
  // 默认文章标签
  async getCategories() {
    return httpService({
      url: '/front/articles/categories'
    })
  },
  // 用户自己定义标签
  async userCategory() {
    return httpService.get('/front/index/user_category')
  },
  // 修改标签
  async setUserCategory(category) {
    return httpService.post('/front/index/user_category', {
      category
    })
  },
  // 获取推荐
  async getRecommend() {
    return httpService.get('/front/index/recommend')
  },
  // 获取文章
  async getArticles(page, cate_id) {
    return httpService.get(`/front/articles`, {
      page,
      cate_id
    })
  },
  // 得到关注的人
  async getFollowUser() {
    return httpService.get('/front/index/follow/user')
  },
  // 得到关注人的文章
  async getFollowArticle(page, author) {
    if (author) {
      return httpService.get('/front/index/follow/article', {
        page,
        author
      })
    } else {
      return httpService.get('/front/index/follow/article', {
        page
      })
    }
  },
  // 轮播图
  async getSlides() {
    return httpService.get('/front/index/slides')
  }
}
