import httpService from '@/service/httpService'
import store from '../../store'
export default {
  // 发送验证码 [1登录 2注册 3改密 4换绑 5设置支付密码]
  sendValidateCode(phone, type) {
    return httpService.post('/send-sms-code', {
      type: type,
      phone_number: phone
    })
  },
  // 验证验证码是否正确
  validateCode(phone, code) {
    return httpService.post('/front/account/validateOldPhone', {
      code: code,
      phone: phone
    })
  },
  // 获取签名
  async getSignature() {
    let res = await httpService.get('/front/get-wechat-config')
    store.commit('system/updateWxSignature', {
      app_id: res.data.config.app_id,
      jsapi_ticket: res.data.ticket.ticket
    })
    return res
  },
  // 绑定手机号
  bindPhone(postObj) {
    return httpService.post('/front/account/bind_phone', postObj)
  },
  // 获取充值脉宝种类
  getMaibao() {
    return httpService.get('/front/maibao/goods')
  },
  // 获取余额
  getRestMoney() {
    return httpService.get('/front/account/wallet')
  },
  // 绑定新手机号
  bindNewPhone(postObj) {
    return httpService.post('/front/account/bindNewPhone', postObj)
  },
  // 异步获取图片base64编码
  getImgBase64(url) {
    return httpService({
      url: '/front/aliyun-img-base',
      loading: false,
      params: {
        path: url
      }
    })
  },
  /* 
    @name shareCount
    @desc 分享统计
    @params
        id      被分享的目标id
        type    1文章2个人微网3名片4营销网页
  */
  shareCount(type, id) {
    httpService.post('/front/share', {
      id,
      type
    })
  },
  // 发送错误报告
  sendError(option) {
    httpService.post('/front/front-log', {
      type: '', //类型
      message: option.message, // 异常信息
      name: option.name, // 异常名称
      script: '', // 异常脚本url
      line: '', // 异常行号
      column: '', // 异常列号
      stack: option.stack, // 异常堆栈信息
    })
  }
}
