export default {
  setToken(state, data) {
    state.token = data
  },
  // ossConfig
  updateOssConfig(state, data) {
    state.ossConfig = data
  },
  // 设置平台
  setPlatform(state, data) {
    state.platform = data
  },
  // 分享人
  updateGuider(state, data) {
    state.guider = data
  },
  // 分享引导
  shareGuide(state, data) {
    state.shareGuide = data
  },
  // 分享引导
  updatePayInfo(state, data) {
    state.payInfo = data
  },
  // 开通会员之后跳转的路径
  updateBeVipPath(state, data) {
    state.beVipPath = data
  },
  // updateRouter
  updateRouter(state, data) {
    switch (data.type) {
      case 'add':
        state.router.push(data.data)
        break
      case 'del':
        state.router = state.router.filter(item => {
          return item !== data.data
        })
        break
      case 'delAll':
        state.router = []
        break
      default:
        state.router = state.router
    }
  },
  updateRedirect(state, data) {
    state.redirect = data
  },
  // 需要裁剪的图片对象
  updateCropper(state, data) {
    state.cropper = data
  },
  // 更新payType
  updatepayType(state, data) {
    state.payType = data
  },
  // 更新请求loading
  updateHttpLoading(state, data) {
    state.httpLoading = data
  },
  // 微信签名配置
  updateWxSignature(state, data) {
    state.signature = data
  }
}
