import store from '../../store'
import httpService from '../../service/httpService'

export default {
  // 获取互推列表
  async getMatchList() {
    let res = await httpService.get('/front/inter_push/list')
    return res
  },
  // 获取自己的互推信息
  async getMatchInfo() {
    let res = await httpService.get('/front/inter_push/get_info')
    if (res.status === 0) {
      let data = {
        name: res.data.name,
        phone: res.data.phone,
        user_id: res.data.user_id,
        use_num: res.data.use_num,
        province: res.data.province,
        share_num: res.data.share_num,
        is_enabled: res.data.is_enabled,
        province_id: res.data.province_id,
        material_id: res.data.material_id,
        head_picture: res.data.head_picture,
        material_style: res.data.material_style,
        forme_brows_num: res.data.forme_brows_num,
        today_share_num: res.data.today_share_num,
        others_browse_num: res.data.others_browse_num,
        share_passive_num: res.data.share_passive_num
      }
      data.material_style = data.material_style
      if (data.material_style === 4) {
        data.material_title = '我的个人网页'
      } else {
        data.material_title = res.data.interPushInfoMaterial.title
      }
      store.commit('circle/updateMatchInfo', { type: 'init', data: data })
    }
    return res
  },
  /* 
    @name       updateMatchInfo
    @desc       更新自己的互推信息
    @params 
        params  Object  互推信息键值对
  */
  async updateMatchInfo(params) {
    let res = await httpService.put('/front/inter_push/info_material', params)
    if (res.status === 0) {
      store.commit('circle/updateMatchInfo', { type: 'update', data: params })
    }
    return res
  },
  /* 
    @name       getCircleList
    @desc       获取互推分享记录、分享任务
    @params     
        type    Number  1相互已经分享，2对方没有分享，3我的分享任务
        page    Number  只有type = 1
  */
  async getCircleList(type, page) {
    let res = await httpService.get('/front/inter_push/share_list', {
      type,
      page
    })
    if (res.status === 0) {
      if (type === 3) {
        store.commit('circle/updateShareTask', {
          type: 'init',
          data: res.data[0].passive_inter_push_record
        })
      }
    }
    return res
  },
  // 根据用户id获取该用户的互推信息
  async getMatchInfoById(id) {
    let res = await httpService.get(`/front/inter_push/info/${id}`)
    return res
  },
  /* 
    @name   shareMatchedInfo
    @desc   开始互推并生成记录
    @params
        params  Object
            user_id         用户id
            material_style  互推素材类型
            material_id     互推素材id
  */
  async shareMatchedInfo(params) {
    let res = await httpService.post('/front/inter_push/add', params)
    return res
  }
}
