export default {
  increment(state) {
    state.count++
  },
  decrement(state) {
    state.count--
  },
  // ossConfig
  updateOssConfig(state, data) {
    state.ossConfig = data
  },
  setUserInfo(state, data) {
    state.userInfo = data
  },
  // 详细信息
  setUserDetailInfo(state, data) {
    state.userDetailInfo = data
  },
  // 当前选择银行卡信息
  setBank(state, data) {
    state.bankCard = data
  },
  // 清除信息
  clearAll(state, data) {
    state.ossConfig = null
    state.count = null
    state.userDetailInfo = null
    state.userInfo = null
    state.bankCard = null
  },
  // 更新用户信息
  updateUser(state, data) {
    let params = data
    for (let p in params) {
      state.userInfo[p] = params[p]
    }
  }
}
