export default {
  // 更新匹配信息
  updateMatchInfo(state, data) {
    switch (data.type) {
      case 'init':
        state.matchInfo = data.data
        break
      case 'update':
        let params = data.data
        for (let p in params) {
          state.matchInfo[p] = params[p]
        }
        break
      default:
      // nothing
    }
  },
  // 更新分享任务
  updateShareTask(state, data) {
    switch (data.type) {
      case 'init':
        state.shareTask = data.data
        break
      case 'del':
        state.shareTask = state.shareTask.filter(item => item.id !== data.data)
        break
      default:
      // nothing
    }
  }
}
