import store from '../store'
import wx from 'weixin-js-sdk'
import circle from '../api/circle'
import system from '../api/system'
import { delHTML } from '../utils'
import dialogService from '../service/dialogService'
import { isIos, pageCover, getArticleCover, getParamsFromUrl } from '@/utils'

// 微信签名随机字符串
const randomChar = function() {
  let nonceStr = ''
  let character = '0123456789qwertyuioplkjhgfdsazxcvbnm'
  for (let i = 0; i < 16; i++) {
    nonceStr += character[Math.ceil(Math.random() * 36)]
  }
  return nonceStr
}

/* 
