      else if (c < 0x800) r.push(0xc0 + ((c >> 6) & 0x1f), 0x80 + (c & 0x3f))
      else {
        if ((x = c ^ 0xd800) >> 10 == 0)
          //对四字节UTF-16转换为Unicode
          (c = (x << 10) + (s.charCodeAt(++i) ^ 0xdc00) + 0x10000),
            r.push(0xf0 + ((c >> 18) & 0x7), 0x80 + ((c >> 12) & 0x3f))
        else r.push(0xe0 + ((c >> 12) & 0xf))
        r.push(0x80 + ((c >> 6) & 0x3f), 0x80 + (c & 0x3f))
      }
