        return (m[1] & m[2]) | (m[1] & m[3]) | (m[2] & m[3])
      },
      function() {
        return m[1] ^ m[2] ^ m[3]
      }
    ],
    rol = function(n, c) {
      return (n << c) | (n >>> (32 - c))
    },
    k = [1518500249, 1859775393, -1894007588, -899497514],
    m = [1732584193, -271733879, null, null, -1009589776]
