/* 
    @nname  微信签名函数
    @desc   微信签名函数
    @params
            apiList array   需要使用api
*/
const signnure = function(apiList) {
  let noncestr = randomChar()
  let timestamp = new Date().getTime()
  let appId = store.state.system.signature.app_id
  let url = isIos() ? window.url : window.location.href
  let jsapi_ticket = store.state.system.signature.jsapi_ticket
  let signature = `jsapi_ticket=${jsapi_ticket}&noncestr=${noncestr}&timestamp=${timestamp}&url=${url}`
  let options = {
    debug: false, // 开启调试模式,调用的所有api的返回值会在客户端alert出来，若要查看传入的参数，可以在pc端打开，参数信息会通过log打出，仅在pc端时才会打印。
    appId: appId, // 必填，公众号的唯一标识
    nonceStr: noncestr, // 必填，生成签名的随机串
    timestamp: timestamp, // 必填，生成签名的时间戳
    signature: sha1(signature), // 必填，签名
    jsApiList: apiList // 必填，需要使用的JS接口列表
  }
  return options
}

const ios = isIos()

const sdkService = {
  config: async function(type) {
    // if (ios && (type !== 'ios' && type !== 'pay')) return
    try {
      let apiList
      let menuList = [
        'menuItem:share:qq',
        'menuItem:copyUrl',
        'menuItem:readMode',
        'menuItem:share:QZone',
        'menuItem:share:email',
        'menuItem:share:weiboApp',
        'menuItem:share:facebook',
        'menuItem:openWithSafari',
        'menuItem:openWithQQBrowser'
