        apiList = ['chooseWXPay', 'hideMenuItems']
        menuList.push('menuItem:share:timeline')
        menuList.push('menuItem:share:appMessage')
      } else if (type === 'share') {
        apiList = [
          'hideMenuItems',
          'onMenuShareTimeline',
          'onMenuShareAppMessage'
        ]
      } else if (type === 'shareTask') {
        apiList = ['hideMenuItems', 'onMenuShareTimeline']
        menuList.push('menuItem:share:appMessage')
      } else {
        apiList = [
          'hideMenuItems',
          'onMenuShareTimeline',
          'onMenuShareAppMessage'
        ]
      }

      let options = signnure(apiList)
      wx.config(options)
      wx.ready(function() {
        wx.hideMenuItems({
          menuList
        })
      })
    } catch (err) {
      console.log(err)
    }
  },
  /*
    options: {
        link: link,                         // 分享链接 *
        desc: desc,                         // 分享描述 *
        title: title,                       // 分享标题 *
        imgUrl: imgUrl,                     // 分享封面 *
        cancel: function() {},
        success: function() {},
        type: '',                           // 分享类型,music、video或link，不填默认为link
        dataUrl: '',                        // 如果type是music或video，则要提供数据链接，默认为空
    }
  */
  share: function(options) {
    wx.onMenuShareTimeline(options)
    wx.onMenuShareAppMessage(options)
  },
  /* 
    @name   articleConfig
    @desc   文章分享配置 
    @params 
            article,
            shareId,
            callback
  */
  articleConfig(article, shareId, callback) {
    let options, task, userId, userInfo
    userInfo = store.state.user.userInfo
    if (userInfo) userId = userInfo.id
    task = getParamsFromUrl('task')
    shareId = shareId || userId
    options = {
      title: article.title,
      desc: delHTML(article.content).substring(0, 40),
      imgUrl: getArticleCover(article.cover),
      link: `${window.host}/?#/article/detail/${article.uuid ||
        article.id}/${shareId}`,
      success: async function() {
        store.commit('system/shareGuide', false)
        system.shareCount(1, article.uuid)
