        }
      }
    }
    return options
  },
  /* 
    @name   cardConfig
    @desc   名片分享配置 
    @params 
            card,
            cardUserId,
            callback
  */
  cardConfig(card, cardUserId, callback) {
    let options, task, userId, userInfo
    userInfo = store.state.user.userInfo
