    task = getParamsFromUrl('task')
    options = {
      imgUrl: card.portrait,
      desc: '汇脉宝——共享流量，专注营销',
      link: `${window.host}/?#/card/detail/${cardUserId}`,
      title: `我是${card.name || card.user_name}，邀请您与我共享千万营销人名片`,
      success: function() {
        store.commit('system/shareGuide', false)
        system.shareCount(3, card.card_id)
        if (task) {
