    userInfo = store.state.user.userInfo
    if (userInfo) userId = userInfo.id
    task = getParamsFromUrl('task')
    let cover = pageCover(webPage)
    options = {
      title: webPage.title,
      desc: webPage.motto || '汇脉宝——共享流量，专注营销',
      link: `${window.host}/?#/webPage/detail/${webPage.id}`,
      imgUrl: /^http/.test(cover) ? cover : `${window.host + cover}`,
      success: function() {
        store.commit('system/shareGuide', false)
        system.shareCount(2, webPage.id)
        if (task) {
          circle.completeShareTask(task)
          if (callback) callback()
        } else {
          if (callback) callback()
        }
      }
    }
    return options
  },
  // 分享营销网页
  marketingConfig(marketing) {
    let options, task, userId, userInfo
    userInfo = store.state.user.userInfo
    if (userInfo) userId = userInfo.id
    task = getParamsFromUrl('task')
    options = {
      title: marketing.title,
      imgUrl: marketing.cover,
      desc: '汇脉宝——共享流量，专注营销',
      link: `${window.host}/?#/marketing/detail/${marketing.id}`,
      success: function() {
        system.shareCount(4, marketing.id)
        store.commit('system/shareGuide', false)
        if (task) {
          circle.completeShareTask(task)
          if (callback) callback()
        } else {
          if (callback) callback()
        }
      }
    }
    return options
  },
  // 默认分享
  defaultShare: function() {
    let options = {
      type: '',
      dataUrl: '',
      title: '汇脉宝',
      link: window.host + '/',
      desc: '汇脉宝——共享流量，专注营销',
      imgUrl: `${window.host}/static/full-logo.png`
    }
    wx.ready(function() {
      wx.onMenuShareTimeline(options)
      wx.onMenuShareAppMessage(options)
    })
  },
  /*
    options: {
        timeStamp: 0,                   // 支付签名时间戳，注意微信jssdk中的所有使用timestamp字段均为小写。但最新版的支付后台生成签名使用的timeStamp字段名需大写其中的S字符
        nonceStr: '',                   // 支付签名随机串，不长于 32 位
        package: '',                    // 统一支付接口返回的prepay_id参数值，提交格式如：prepay_id=\*\*\*）
        signType: '',                   // 签名方式，默认为'SHA1'，使用新版支付需传入'MD5'
        paySign: '',                    // 支付签名
        success: function(res) {}
    }
  */
  pay: function(options) {
    wx.chooseWXPay({
      timestamp: options.timeStamp,
      nonceStr: options.nonceStr,
      signType: options.signType,
      package: options.package,
      paySign: options.paySign,
      success: function(res) {
        options.success(res)
      },
      fail: function(res) {
        dialogService.alert(res)
      }
    })
  }
}

wx.error(function(res) {
  console.log(res)
})

export default sdkService
