import httpService from '@/service/httpService'
import store from '../../store/index'

export default {
  // 得到信息
  async getProfile() {
    let res = await httpService.get(`/front/profile`)
    if (res.status === 0) {
      store.commit('user/setUserInfo', res.data)
    } else {
      throw new Error(res.data.message)
    }
    return res
  },
  // 得到详细信息
  async getDetailInfo() {
    let res = await httpService.get(`/front/profile/info`)
    if (res.status === 0) {
      store.commit('user/setUserDetailInfo', res.data)
      return res
    } else {
      throw new Error(res.data.message)
    }
  },
  // 注册送一元
  async registReward() {
    await httpService({
      url: '/front/user_activity_amount',
      loading: false,
      method: 'post',
      body: {
        amount: 1
      }
    })
  },

  // 修改个人信息
  async putDetailInfo(data) {
    let res = await httpService.post(`/front/update-user-info`, data)
    if (res.status === 0) {
      store.commit('user/setUserDetailInfo', res.data)
      return res
    } else {
      return res
    }
  },
  // 获取营销榜单
  async getMarketingRank(page, limit = 10) {
    let res = await httpService.get('/front/personal_page/rank', {
      page,
      limit
    })
    return res
  },
  // 获取收入榜单
  async getIncomeRank(limit) {
    let res = await httpService.get('/app/reward/rank', {
      limit
    })
    return res
  },
  // 获取赠送脉宝列表
  async getFriend() {
    let res = await httpService.get('/front/maibao/friend')
    return res
  },
  // 获取赠送脉宝列表
  async attention(id) {
    let res = await httpService.post('/front/maibao/remind/' + id)
    return res
  },
  // 获取vip信息
  async getVip() {
    let res = await httpService.get('/front/profile/vip')
    return res
  },
  // 获取赠送脉宝列表
  async sendMaibao(id, amount) {
    let res = await httpService.post('/front/maibao/give/' + id, {
      amount
    })
    return res
  },
  // 删除银行卡
  async delBank(id) {
    let res = await httpService.delete('/front/bank-card/' + id)
    return res
  },
  // 营销标签
  async label() {
    let res = await httpService.get('/front/profile/label')
    return res
  },
  // 添加标签
  async addLabel(item) {
    let res = await httpService.post('/front/profile/label', {
      label: item
    })
    return res
  },
  // 是否公开手机号
  toggleShowPhone(val) {
    httpService({
      method: 'post',
      loading: false,
      body: { is_show_phone: val },
      url: '/front/profile/is-show-phone'
    })
  }
}
