import card from '../../../api/card'
import { mapState, mapActions } from 'vuex'
import Wrapper from '../../../components/Wrapper'
import CardItem from '../../../components/CardItem'
import AppHeader from '../../../components/AppHeader'
import AppLoading from '../../../components/AppLoading'
import dialogService from '../../../service/dialogService'
import AppBottomOperation from '../../../components/AppBottomOperation'
export default {
  data() {
    return {
      key: '', // 关键字
      cards: [], // 名片夹名片列表
      limit: 10, // 步长
      offset: 0, // 起始位置
      total: null, // 总条数
      isEdit: false, // 是否编辑
      touchTime: null, // 长按时间
      loading: false, // 是否正在请求
      isSelectAll: false, // 是否全选
      windowHeight: null, // 窗口高度
      gloableLoading: true // 全局加载
    }
  },
  mounted() {
    this.getCollectCard()
    this.windowHeight = document.documentElement.clientHeight
  },
  beforeRouteLeave(to, from, next) {
    if (this.isEdit) {
      this.isEdit = !this.isEdit
    } else {
      if (!/^\/card\/detail/.test(to.path)) {
        this['system/updateRouter']({
          type: 'del',
          data: 'articleCollection'
        })
      }
      next(true)
    }
  },
  methods: {
    ...mapActions(['system/updateRouter']),
    // 获取收藏的名片
    async getCollectCard() {
      try {
        this.loading = true
        let res = await card.getCollectCard({
          limit: this.limit,
          keyword: this.key,
          offset: this.offset
        })
        if (res.status !== 0) throw new Error(res.message)
        if (this.offset === 0) {
          this.cards = res.data.data
        } else {
          this.cards = this.cards.concat(res.data.data)
        }
        this.gloableLoading = false
        this.loading = false
        this.total = res.data.count
        this.offset = this.offset + res.data.data.length
      } catch (err) {
        dialogService.alert(err.message)
      }
    },
    // 长按开始
    touchStart() {
      this.touchTime = new Date().getTime()
      if (!this.isEdit) {
        setTimeout(() => {
          this.isEdit = this.touchTime ? true : false
        }, 500)
      }
    },
    touchMove() {
      this.touchTime = null
    },
    // 长按结束
    touchEnd(index) {
      if (!this.touchTime) return
      let touchTime = new Date().getTime()
      if (touchTime - this.touchTime > 20 && touchTime - this.touchTime < 300) {
        if (this.isEdit) {
          this.$set(this.cards[index], 'select', !this.cards[index].select)
        } else {
          this.$router.push(`/card/detail/${this.cards[index].user_id}`)
        }
      }
      this.touchTime = null
      let selectd = this.cards.filter(item => item.select)
      this.isSelectAll = this.cards.length === selectd.length
    },
    // 滚动处理函数
    scrollHandler(scrolled, scrollHeight) {
      if (this.loading) return false
      let scrollBottom = this.windowHeight + scrolled + 40 > scrollHeight
      let shouldLoad = scrollBottom && this.offset < this.total
      if (shouldLoad) this.getCollectCard()
    },
    // 全选
    selectAll() {
      this.isSelectAll = !this.isSelectAll
      this.cards = this.cards.map(item => {
        item.select = this.isSelectAll
        return item
      })
    },
    // 删除所选名片
    delCards() {
      let _self = this
      dialogService.confirm('您确认删除已选名片', async function(del) {
        if (del) {
          try {
            let dels = _self.cards
              .filter(item => item.select)
              .map(item => item.id)
            let res = await card.cancleCollecte(dels)
            if (res.status !== 0) throw new Error(res.message)
            _self.cards = _self.cards.filter(item => !item.select)
            _self.offset = _self.offset - dels.length
            _self.total = _self.total - dels.length
            dialogService.toast('删除成功')
            this.isEdit = false
          } catch (err) {
            dialogService.alert(err.message)
          }
        }
      })
    },
    // 搜索
    inputHandler() {
      let key = this.key
      //   if (!key) return
      setTimeout(() => {
        if (key === this.key) {
          this.cards = []
          this.offset = 0
          this.total = null
          this.getCollectCard()
          this.gloableLoading = true
        }
      }, 500)
    }
  },
  computed: {
    ...mapState({
      platform: state => state.system.platform
    }),
    paddingTop: function() {
      if (this.isEdit) {
        return this.platform !== 'wechat' ? '118px' : '0px'
      } else {
        return this.platform !== 'wechat' ? '198px' : '80px'
      }
    }
  },
  components: {
    Wrapper,
    AppLoading,
    CardItem,
    AppHeader,
    AppBottomOperation
  }
}
