import { mapState, mapActions } from 'vuex'
import VideoItem from '@/components/VideoItem'
import httpService from '@/service/httpService'
import state from '../../store/modules/user/state'
import dialogService from '@/service/dialogService'

export default {
  props: ['type', 'item', 'indexa', 'url'],
  data() {
    return {
      bgstate: true, // 背景选择
      ratio: {}, // 单前所选比例
      show: false, // 上滑动画状态
      toggles: [], // 可切换的组件
      category: {}, // 当前所选分类
      itemCopy: {}, // 传入组件的副本
      components: [], // 单前显示的组件
      marketPages: [], // 营销网页列表
      filterStyle: {}, // 过滤条件的高度
      stype: '', // 所选类型
      url2: '', // 背景类型1
      url3: '', // 背景类型2
      filters: [
        // 比例筛选
        { text: '1:1', val: '1.00', className: 'iconfont icon-bi4' },
        { text: '2:3', val: '0.66', className: 'iconfont icon-bi' },
        { text: '3:2', val: '1.50', className: 'iconfont icon-bi3' },
        { text: '4:3', val: '1.33', className: 'iconfont icon-bi5' },
        { text: '3:4', val: '0.75', className: 'iconfont icon-bi1' },
        { text: '9:16', val: '0.56', className: 'iconfont icon-bi6' },
        { text: '16:9', val: '1.77', className: 'iconfont icon-bi2' }
      ],
      colorarr: [
        '#e3ffe1',
        '#ffd2f1',
        '#dfffff',
        '#fffbee',
        '#fff3f3',
        '#ffffff',
        '#c5ffc9',
        '#efd2ff',
        '#b8f1ff',
        '#ffeedf',
        '#ffdfdf',
        '#808080',
        '#93d1ad',
        '#d7ccec',
        '#a5faff',
        '#eccfaf',
        '#ffc7c7',
        '#484848'
      ],
      videoNav: ['发布视频', '管理视频'], //视频管理
      videoList: [], // 视频列表
      videoIndex: 0, // 导航索引
      mediaList: [
        {
          src: '/static/tx.png',
          text: '腾讯视频',
          link: 'https://v.qq.com'
        },
        {
          src: '/static/yk.png',
          text: '优酷视频',
          link: 'https://www.youku.com'
        },
        {
          src: '/static/aqy.png',
          text: '爱奇艺',
          link: 'http://m.iqiyi.com'
        },
        {
          src: '/static/sh.png',
          text: '搜狐视频',
          link: 'https://m.tv.sohu.com/'
        }
      ], //视频列表
      text: '',
      showStepTwo: false, // 是否是第2步
      videoInfo: {
        // 视频信息
        des: '',
        img: '',
        swf: '',
        title: ''
      },
      searchName: '', // 按名字搜索
      piclist: [],
      page: 1, // 默认第一页
      maxPage: 1, //默认最大页面
      msg: '加载更多',
      bgcolor: ''
    }
  },
  mounted() {
    this.bgcolor = this.url
    // this.init()
  },
  methods: {
    ...mapActions([
      'webPage/updateSelectShow',
      'webPage/updateComponentCate',
      'webPage/updateCardInfo',
      'webPage/updateTempDatas',
      'webPage/updateEditVideo',
      'webPage/updateVideoIndex'
    ]),
    selectColor(x) {
      this.bgcolor = x
      this.bgstate = false
      this.submit('bgpic')
    },
    selecPic(x) {
      this.bgstate = true
      this.url2 = "url(' " + x + " ') no-repeat fixed center / 100% 100%"
      this.url3 = x
      this.submit('bgpic')
    },
    Jump(item) {
      this['webPage/updateEditVideo'](true)
      this['webPage/updateTempDatas'](this.$parent.$parent.datas)
      this['webPage/updateVideoIndex'](this.indexa)
      this.closeSelect() // 必须关闭，不然就弹窗不出来
      window.location.href = item.link
    },
    // 加载更多
    more() {
      this.page++
      if (this.page <= this.maxPage) {
        this.listMovie()
      }
    },
    // 切换tab
    async tabIndex(index) {
      this.videoIndex = index
      if (this.videoIndex == 1) {
        this.searchName = ''
        this.page = 1
        this.videoList = []
        this.listMovie()
      }
    },
    // 获取视频样式
    async listMovie() {
      this.msg = '加载中'
      try {
        let res = await httpService({
          url: '/front/list-movie',
          loading: false,
          params: {
            page: this.page
          }
        })
        if (res.status !== 0) {
          throw new Error(res.message)
        }
        this.page = res.data.current_page
        this.maxPage = res.data.last_page
        if (this.page < this.maxPage) {
          this.msg = '加载更多'
        } else {
          this.msg = '没有更多'
        }
        this.videoList =
          this.page === 1 ? res.data.data : this.videoList.concat(res.data.data)
      } catch (error) {
        dialogService.alert(error.message)
      }
    },
    // 搜索视频
    async search(val) {
      try {
        let res = await httpService({
          url: '/front/search-movie',
          method: 'get',
          params: {
            search: this.searchName
          },
          loading: false
        })
        if (res.status !== 0) {
          throw new Error(res.message)
        }
        this.videoList = res.data.data
      } catch (error) {
        dialogService.alert(error.message)
      }
    },
    // 阻止冒泡
    doNothing() {
      // nothing
    },
    // 下一步
    async next() {
      try {
        let res = await httpService({
          url: '/front/get-movie',
          body: {
            url: this.text
          },
          method: 'post',
          loading: false
        })
        if (res.status !== 0 || !res.data) {
          this.text = ''
          throw new Error('视频获取失败')
        }
        this.showStepTwo = true
        this.videoInfo = res.data
        // this.item.video.img.src = res.data.img
      } catch (error) {
        dialogService.alert(error.message)
      }
    },
    // init
    async init() {
      try {
        // this.$refs.wrapper.style.marginTop = 3.33333 + 'rem'
        if (this.type === 'video') {
          this.$refs.wrapper.style.marginTop = '90px'
        }
        if (this.type === 'layout') {
          this.itemCopy = JSON.parse(JSON.stringify(this.item))
          let params = {
            ratio: this.itemCopy.ratio,
            cate_id: this.itemCopy.cate,
            type: this.itemCopy.layoutType
          }
          await this.getComponents(params)
        } else if (this.type === 'superLink') {
          this.getMarketPages()
          this.itemCopy = JSON.parse(JSON.stringify(this.item))
        } else if (this.type === 'bgcolor') {
          let _self = this
          if (this.piclist.length === 0) {
            this.getpiclist()
          }
        } else {
          this.itemCopy = null
          await this.getComponentCate()
        }
      } catch (err) {
        dialogService.alert(err.message)
      }
    },
    // 获取背景图片
    async getpiclist() {
      let res = await httpService.get('/front/personal_page/customs/background')
      this.piclist = res.data
    },
    // 获取组件分类[如果有组件分类则直接获取]
    async getComponentCate() {
      if (this.componentCate.length) {
        if (!this.category.id) this.selectCate(0)
        return false
      }
      let res = await httpService.get('/front/personal_page/custom/categories')
      this['webPage/updateComponentCate'](res.data)
    },
    // 选择一个分类
    selectCate(index) {
      this.category = this.componentCate[index]
      if (this.category.type === 'imgText' || this.category.type === 'pics') {
        this.filterStyle = {
          height: '256px'
        }
      } else {
        this.ratio = {}
        this.hideFilter()
      }
      this.stype = this.componentCate[index].type
      if (this.componentCate[index].active && this.components.length) return
      let componentCate = this.componentCate.map((it, i) => {
        let obj = Object.assign({}, it)
        obj.active = i === index
        return obj
      })
      this['webPage/updateComponentCate'](componentCate)
      if (this.category.type === 'bgColor') {
        if (this.piclist.length === 0) {
          this.getpiclist()
        }
        return
      } else {
        this.getComponents()
      }
    },
    // 筛选图片比例|筛选图片排列方式
    selectPattern(type, index) {
      if (type === 'imgText' || type === 'pics') {
        this.ratio = this.filters[index]
      }
      this.getComponents()
    },
    // 获取当前组件分类下的组件[需要检查他们是否具有筛选项]
    async getComponents(input) {
      try {
        const Params = {
          ratio: this.ratio.val,
          cate_id: this.category.id
        }
        let params = input || Params
        let res = await httpService({
          url: '/front/personal_page/custom/components',
          params: params
        })
        input ? (this.toggles = res.data) : (this.components = res.data)
      } catch (err) {
        dialogService.alert(err.message)
      }
    },
    // 获取营销网页列表
    async getMarketPages() {
      if (!this.marketPages.length) {
        let res = await httpService.get('/front/user-marketing')
        this.marketPages = res.data.data
      }
    },
    // 选择一个营销网页
    selectMarketPage(index) {
      this.marketPages = this.marketPages.map((item, _index) => {
        if (index === _index) {
          item.selected = !item.selected
        } else {
          item.selected = false
        }
        return item
      })
    },
    // 选择当前组件
    async selectThis(type, item) {
      // 构建一个理想组件[添加和转化组件布局]
      const _makeComponnet = function(x) {
        let component = JSON.parse(item.content)
        component.id = item.id
        component.cate = item.cate
        component.price = item.price
        component.ratio = item.ratio
        component.hasVip = item.has_vip
        component.layoutType = item.type
        if (x) {
        }
        return component
      }
      if (type === 'add') {
        setTimeout(() => {
          this.closeSelect()
        }, 500)
        let component = _makeComponnet()
        // 如果是基础信息则自动填充

        if (component.type === 'baseInfo') {
          component = await this.autoFill(component)
        }
        this.$parent.$parent.selectComponent(component)
      } else if (type === 'toogle') {
        let component = _makeComponnet()
        component.title = this.itemCopy.title
        component.subTitle = this.itemCopy.subTitle
        console.warn('切换图文样式可能存在隐患，具体什么问题暂时不清楚')
        component.infos = JSON.parse(JSON.stringify(this.itemCopy.infos))
        component.children = JSON.parse(JSON.stringify(this.itemCopy.children))
        this.itemCopy = JSON.parse(JSON.stringify(component))
        // this.$parent.$parent.selectComponent(component)
      } else if (type === 'superLink') {
        this.itemCopy.children = this.itemCopy.children.map((it, index) => {
          if (item === index) it.selected = !it.selected
          return it
        })
      } else if (type === 'bgcolor') {
        this.$parent.$parent.changebgcolor(item)
        this.closeSelect()
      } else if (type === 'bgpic') {
        this.$parent.$parent.changebgpic(item)
        this.closeSelect()
      }
    },
    // 自动填充基础信息
    async autoFill(component) {
      try {
        let cardInfo = null
        if (!this.cardInfo) {
          let res = await httpService.get('/front/card', {
            user_id: this.user.id
          })
          if (res.status === 0) {
            this['webPage/updateCardInfo'](res.data)
          }
          cardInfo = res.data
        } else {
          cardInfo = this.cardInfo
        }
        component.infos.name = cardInfo.name
        component.infos.jobs = cardInfo.profession
        component.infos.company = cardInfo.company
        component.infos.avatar.href = cardInfo.portrait
        return component
      } catch (err) {
        // 不做处理
        console.warn(err.message)
        return component
      }
    },
    // 隐藏筛选条件
    hideFilter() {
      this.filterStyle = {
        height: 0
      }
    },
    // 隐藏显示
    isHide() {
      if (this.filterStyle.height) {
        this.hideFilter()
      } else {
        this.filterStyle = {
          height: '256px'
        }
      }
    },
    // 提交确定[切换布局和添加超链接]
    submit(type) {
      if (type === 'toogle') {
        this.closeSelect()
        this.$parent.$parent.editComponent(this.indexa, this.itemCopy)
      } else if (type === 'bgpic') {
        if (this.bgstate) {
          if (this.url3.length > 0) {
            this.$parent.$parent.changebgpic(this.url3)
          }
        } else {
          this.selectThis('bgcolor', this.bgcolor)
        }
        this.closeSelect()
      } else {
        let superLink = { text: '', link: '' }
        this.marketPages.forEach(item => {
          if (item.selected) {
            superLink = {
              text: item.title,
              link: `/marketing/detail/${item.id}`
            }
          }
        })
        this.itemCopy.children.forEach(item => {
          if (item.selected) {
            item.img.superLink = superLink
            delete item.selected
          }
        })
        this.closeSelect()
        this.$parent.$parent.editComponent(this.indexa, this.itemCopy)
      }
    },
    // 关闭组件选择
    closeSelect() {
      this.show = false

      if (this.type == 'video') {
        this.showStepTwo = false
        this.videoIndex = 0
        this.text = ''
        this.videoInfo = {}
      }

      this['webPage/updateSelectShow'](false)
    },
    // 上一步
    pre() {
      this.showStepTwo = false
    },
    // 保存
    async save() {
      try {
        let res = null
        if (this.videoInfo.id) {
          res = await httpService.post('/front/edit-movie', {
            id: this.videoInfo.id,
            title: this.videoInfo.title,
            description: this.videoInfo.des,
            img: this.videoInfo.img,
            swf: this.videoInfo.swf,
            url: this.videoInfo.url
          })
        } else {
          res = await httpService.post('/front/add-movie', {
            title: this.videoInfo.title,
            description: this.videoInfo.des,
            img: this.videoInfo.img,
            swf: this.videoInfo.swf,
            url: this.text
          })
        }
        if (res.status !== 0) {
          dialogService.alert(err.message)
        }
        this.text = ''
        this.item.video.title = this.videoInfo.title
        this.item.video.content = this.videoInfo.des
        this.item.video.link = this.videoInfo.swf
        this.item.video.img.src = this.videoInfo.img
        this.closeSelect()
      } catch (err) {
        dialogService.alert(err.message)
      }
    },
    childValue(val) {
      // 编辑的视频
      this.videoInfo = val
      this.videoInfo.des = val.description
      this.showStepTwo = true
      this.videoIndex = 0
    },
    //选择的视频
    selectValue(val) {
      this.item.video.title = val.title
      this.item.video.content = val.description
      this.item.video.link = val.swf
      this.item.video.img.src = val.img
      this.closeSelect()
    },
    // 删除视频
    del(id, index) {
      let self = this
      httpService({
        url: '/front/delete-movie',
        method: 'get',
        loading: false,
        params: {
          id: id
        }
      })
        .then(() => {
          self.videoList.splice(index, 1)
          self.page = 1
          self.listMovie()
        })
        .catch(err => {
          dialogService.alert(err.message)
        })
    }
  },
  computed: {
    ...mapState({
      user: state => state.user.userInfo,
      selectShow: state => state.webPage.selectShow,
      cardInfo: state => state.webPage.cardInfo,
      componentCate: state => state.webPage.componentCate
    })
  },
  components: {
    VideoItem
  },
  watch: {
    selectShow: function(newVal, oldVal) {
      if (newVal) {
        this.init()
      }
      setTimeout(() => {
        this.show = newVal
      })
    }
  }
}
