import {
  mapActions
} from 'vuex'
import User from '../../../api/user'
import Login from '../../../api/login'
import System from '../../../api/system'
import validate from '../../../utils/validate'
import dialogService from '../../../service/dialogService.js'

export default {
  data() {
    return {
      active: true, //是否可以点击
      tips: '获取验证码', // 倒计时提示
      params: {
        phone: '',
        password: '',
        code: ''
      },
      isRequest: false // 是否正在请求
    }
  },
  methods: {
    ...mapActions(['system/setToken']),
    // 倒计时
    countdown() {
      let second = 60
      this.active = false
      this.tips = second + 's'
      this.timer = setInterval(() => {
        second--
        this.tips = second + 's'
        if (second === 0) {
          clearInterval(this.timer)
          this.active = true
          this.tips = '获取验证码'
        }
      }, 1000)
    },
    // 获取验证码 type发送验证码 1登录 2注册 3改密 4换绑 5设置支付密码
    async getCode() {
      if (!this.active) return false
      let check = validate.phone(this.params.phone)
      if (!check.status) {
        dialogService.toast(check.msg)
        return false
      }
      try {
        let res = await System.sendValidateCode(this.params.phone, 1)
        if (res.status !== 0) throw new Error(res.message)
        dialogService.toast('发送成功')
        this.countdown()
      } catch (err) {
        dialogService.toast(err.message)
      }
    },
    // 提交
    async submit() {
      if (this.isRequest) return false
      try {
        let phoneValidate = validate.phone(this.params.phone)
        if (!phoneValidate.status) {
          throw new Error(phoneValidate.msg)
        }
        if (!this.params.code) throw new Error('请输入验证码')
        if (!this.params.password) throw new Error('请输入密码')
        if (this.params.password.length < 6 || this.params.password.length > 12)
          throw new Error('密码长度为6-12位')
        this.isRequest = true
        let res = await Login.retrievePwd(this.params)
        if (res.status === -1) {
          throw new Error(res.message)
        }
        this['system/setToken'](res.data.token)
        await User.getProfile()
        this.isRequest = false
        // 考虑转发的后
        //   let a = this.redirect
        //   this.clearStore()
        //   this.updateUser(userRes.data)
        //   if (a) {
        //     this.updateredirect(null)
        //     this.$router.push(a)
        //   } else {
        //     this.$router.push('/article')
        //   }
        this.$router.push('/index')
      } catch (error) {
        this.isRequest = false
        dialogService.toast(error.message)
      }
    }
  }
}
