import validate from '../../../utils/validate'
import System from '../../../api/system'
import Login from '../../../api/login'
import dialogService from '../../../service/dialogService.js'
import { mapState } from 'vuex'

export default {
  data() {
    return {
      active: true, //是否可以点击
      tips: '获取验证码', // 倒计时提示
      isRequest: false, // 是否正在请求
      bindPhoneParams: {
        // 绑定手机需要的参数
        phone: '', // 电话
        union_id: '',
        wechat_name: '',
        head_portrait: '',
        code: '', // 验证码
        open_id: ''
      }
    }
  },
  computed: {
    ...mapState(['user'])
  },
  mounted() {
    if (this.user.wechatInfo) {
      this.bindPhoneParams.union_id = this.user.wechatInfo.unionid
      this.bindPhoneParams.wechat_name = this.user.wechatInfo.nickname
      this.bindPhoneParams.head_portrait = this.user.wechatInfo.headimgurl
      this.bindPhoneParams.open_id = this.user.wechatInfo.open_id
    }
  },
  methods: {
    // 倒计时
    countdown() {
      let second = 60
      this.active = false
      this.tips = second + 's'
      this.timer = setInterval(() => {
        second--
        this.tips = second + 's'
        if (second === 0) {
          clearInterval(this.timer)
          this.active = true
          this.tips = '获取验证码'
        }
      }, 1000)
    },
    // 获取验证码 type发送验证码 1登录 2注册 3改密 4换绑 5设置支付密码
    async getCode() {
      if (!this.active) return
      let check = validate.phone(this.bindPhoneParams.phone)
      if (!check.status) {
        dialogService.toast(check.msg)
        return false
      }
      try {
        let res = await System.sendValidateCode(this.bindPhoneParams.phone, 2)
        if (res.status !== 0) throw new Error(res.message)
        dialogService.toast('发送成功')
        this.countdown()
      } catch (err) {
        dialogService.toast(err.message)
      }
    },
    // 确定
    async submit() {
      if (this.isRequest) return false
      try {
        let phoneValidate = validate.phone(this.bindPhoneParams.phone)
        if (!phoneValidate.status) {
          throw new Error(phoneValidate.msg)
        }
        if (!this.bindPhoneParams.code) throw new Error('请输入验证码')
        this.isRequest = true
        let res = await Login.bindPhone(this.bindPhoneParams)
        if (res.status === -1) {
          throw new Error(res.message)
        }
        this.isRequest = false
        this.$router.push('/index')
      } catch (error) {
        this.isRequest = false
        dialogService.toast(error.message)
      }
    }
  }
}
