.maskMoney {
  position: fixed;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.7);
  z-index: 4;
  top: 0;
  left: 0;
  .wallet {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: auto;

    img {
      width: 668px;
    }

    .close {
      position: absolute;
      top: -30px;
      right: 46px;
      width: 52px;
    }

    .info {
      position: absolute;
      top: 40px;
      width: 100%;
      color: #fae8c4;
      font-size: 48px;
      text-align: center;
      font-weight: 400;

      p {
        line-height: 72px;

        span {
          color: #ffbc37;
          font-size: 60px;
        }
      }

      .draw {
        width: 100%;
        margin-top: 30px;

        a {
          color: #ff6c68;
          font-size: 36px;
          padding: 12px 88px;
          background: #fff;
          border-radius: 44px;
        }
      }
    }
  }
}
.mask {
  @keyframes showtip {
    from {
      opacity: 0;
      transform: scale(0);
    }
    to {
      transform: scale(1);
      opacity: 1;
    }
  }
  .pics {
    animation: showtip 0.3s ease-in-out;
    width: 500px;
    height: 700px;
    border-radius: 6px;
    object-fit: cover;
    position: fixed;
    top: 50%;
    left: 50%;
    margin-top: -400px;
    margin-left: -250px;
  }
  .icon-cuo1 {
    color: #dadada;
    font-size: 50px;
    position: fixed;
    top: 50%;
    left: 50%;
    margin-left: -20px;
    margin-top: 330px;
  }
  z-index: 3;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(#000, 0.5);
}
