result:
.warpper {
  .message {
    .msgbox {
      margin-left: 30px;
      flex: 1;

      .type {
        .time {
          float: right;
          color: #6e6e6e;
          font-size: 22px;
          font-weight: normal;
        }

        overflow: hidden;
        color: #000000;
        font-size: 30px;
        /* font-weight: bold; */
        margin-bottom: 13px;
      }

      .content {
        .reddot {
          float: right;
          display: block;
          width: 20px;
          height: 20px;
          border-radius: 50%;
          border: 3px solid #fff;
          background: #ef2323;
        }

        overflow: hidden;
        position: relative;
        color: #6e6e6e;
        font-size: 24px;
      }
    }

    .icon-tongzhi,
    img {
      text-align: center;
      background: #3599f6;
      position: relative;
      width: 90px;
      height: 90px;
      display: block;
      line-height: 90px;
      color: #fff;
      border-radius: 50%;
      font-size: 40px;
    }

    transition: all 0.3s ease-in-out;
    display: flex;
    align-items: center;
    position: relative;
    width: 100%;
    padding: 0 30px;
    box-sizing: border-box;
    height: 140px;
    border-bottom: 1px solid #f5f5f5;

    .del {
      position: absolute;
      text-align: center;
      right: -155px;
      top: 0;
      width: 155px;
      height: 140px;
      line-height: 140px;
      font-size: 28px;
      color: #fff;
      background: #f50303;
    }
  }

  .topbar {
    .item {
      font-size: 22px;
      color: #666666;
      padding-top: 30px;
      width: 100%;
      text-align: center;

      .icon-shoucang2 {
        background: linear-gradient(to right, #fb4646, #fa4545);
      }

      .icon-woderenqi {
        background: linear-gradient(to right, #fb9635, #fb9535);
      }

      .icon-xitongxiaoxi {
        background: linear-gradient(to right, #385df9, #365cf8);
      }

      .iconfont {
        .reddot {
          position: absolute;
          display: block;
          width: 20px;
          height: 20px;
          border-radius: 50%;
          border: 3px solid #fff;
          background: #ef2323;
          top: 0;
          right: 0;
        }

        position: relative;
        width: 90px;
        height: 90px;
        display: block;
        margin: 0 auto 16px;
        line-height: 90px;
        color: #fff;
        border-radius: 50%;
        font-size: 40px;
      }
    }

    padding: 0 30px;
    height: 210px;
    width: 100%;
    box-sizing: border-box;
    overflow: hidden;
    display: flex;
    border-bottom: 20px solid #f6f6f6;
  }

  color: #000;
  width: 100%;
  box-sizing: border-box;
  min-height: 100vh;
  background: #fff;
  overflow: hidden;
  position: relative;
}
